document.addEventListener("DOMContentLoaded",function(){const t=document.getElementById("menu-toggle"),e=document.querySelector("menu");t.addEventListener("click",function(t){e.classList.toggle("show"),t.stopPropagation()}),document.addEventListener("click",function(n){!e.contains(n.target)&&n.target!==t&&e.classList.remove("show")}),window.addEventListener("resize",function(){window.innerWidth>768&&e.classList.remove("show")})})